package javapack;

public class PyramidNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int r,sp,p,n = 0,num = 0;
for( r=1;r<=num;r++)
{
	for( sp=1;sp<=n;sp++)
	{
		System.out.println(" ");
	}
	n--;
	for( p=1;p<=r;p++)
	{
	System.out.println(r);
	}
	System.out.println("\n");
	}
}
}
